mapper.update:
update patient_record_img
set session_id = #{sessionId,jdbcType=VARCHAR},
  img_id = #{imgId,jdbcType=BIGINT},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select r.session_id,ui.imgUrl,ui.thumbnaillUrl from patient_record r 
INNER JOIN patient_record_img  ri on r.session_id=ri.session_id 
INNER JOIN upload_img ui on ri.img_id=ui.id
where r.patient_id =#{patientId,jdbcType=VARCHAR}
and session_id = #{sessionId,jdbcType=VARCHAR}
order by r.status asc,r.create_time desc
